<script setup>
import { onMounted, onUnmounted, ref } from 'vue'

const expertiseList = ref([
  "Plus de 10 ans d'expérience en administration d'entreprise",
  "Équipe de professionnels certifiés et qualifiés",
  "Solutions adaptées à tous types d'entreprises",
  "Support continu et accompagnement personnalisé"
])

let observer = null

const setupIntersectionObserver = () => {
  observer = new IntersectionObserver(
    (entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          entry.target.classList.add('active')
          observer.unobserve(entry.target) // on ne rejoue pas l’anim
        }
      })
    },
    { threshold: 0.1, rootMargin: '0px 0px -50px 0px' }
  )
}

onMounted(() => {
  setupIntersectionObserver()

  const animatedElements = document.querySelectorAll(
    '.animate-fade-in-up, .animate-fade-in-right, .animate-slide-up, .animate-slide-up-delay, .animate-slide-up-delay-2, .animate-on-scroll'
  )

  animatedElements.forEach((el) => observer.observe(el))
})

onUnmounted(() => {
  if (observer) observer.disconnect()
})
</script>